import { Component, inject } from "@angular/core";
import { CommonModule } from "@angular/common";
import { HousingLocationComponent } from "../housing-location/housing-location.component";
import { Housinglocation } from "../housinglocation";
import { HousingService } from "../housing.service";
import { RouterModule } from "@angular/router";

@Component({
  selector: "app-home",
  standalone: true,
  imports: [CommonModule, HousingLocationComponent, RouterModule],
  template: `
    <section>
      <form>
        <input type="text" placeholder="Filter by city" #filtred />
        <button
          class="primary"
          type="button"
          (click)="filterList(filtred.value)"
        >
          Search
        </button>
      </form>
    </section>
    <section class="results">
      <app-housing-location
        *ngFor="let housingLocation of housingLocationList"
        [housingLocation]="housingLocation"
      ></app-housing-location>
    </section>
  `,
  styleUrls: ["./home.component.css"],
})
export class HomeComponent {
  housingLocationList: Housinglocation[] = [];
  housingService: HousingService = inject(HousingService);
  tmp: Housinglocation[] = [];

  constructor() {
    this.housingLocationList = this.housingService.getAllHousingLocations();
    this.tmp = this.housingService.getAllHousingLocations();
  }
  filterList(value: string) {
    if (value) {
      this.housingLocationList = this.tmp.filter((item) =>
        item?.city.toLocaleLowerCase().includes(value.toLowerCase())
      );
    } else {
      this.housingLocationList = this.housingService.getAllHousingLocations();
    }
  }
}
